graphql.query:
mutation EvaluateRuleInstance ($id: ID!) {
  evaluateRuleInstance (
    id: $id
  ) {
    outputs {
      name
      value
    }
  }
}

query ListAlertInstances {
  listAlertInstances {
        instances {
            accountId
            createdOn
            dismissedOn
            id
            lastEvaluationBeginOn
            lastEvaluationEndOn
            lastEvaluationResult {
                outputs {
                    name
                    value
                }
                rawDataDescriptors {
                    name
                    persistedResultType
                    recordCount
                }
            }
            lastUpdatedOn
            level
            questionRuleInstance {
                accountId
                deleted
                id
                latest
                latestAlertId
                latestAlertIsActive
                name
                operations {
                    actions
                    when 
                }
                outputs
                pollingInterval
                specVersion
                tags
                templates
                type
                version
            }
            reportRuleInstance {
                id
            }
            ruleId
            status
        }
        pageInfo {
            endCursor
            hasNextPage
        }
    }
  }

