graphql.query:
query ListQuestions(
  $mustTags: [String]
  $shouldTags: [String]
  $type: ListQuestionsType
  $limit: Int
  $cursor: String
) {
  questions(
    mustTags: $mustTags
    shouldTags: $shouldTags
    type: $type
    limit: $limit
    cursor: $cursor
  ) {
    questions {
      id
      title
      description
      tags
      queries {
        name
        query
        version
        resultsAre
      }
      variables {
        name
        required
        default
      }
      compliance {
        type
        standard
        requirements
        controls
        details {
          name
          description
        }
      }
      accountId
      integrationDefinitionId
    }
    totalHits
    pageInfo {
      endCursor
      hasNextPage
    }
  }
}

